    <script>
console.log('Script loaded');

        function openFilterModal() {
            console.log('Opening modal');
            const modal = document.getElementById('filterModal');
            const content = document.getElementById('modalContent');
            
            if (modal && content) {
                modal.style.display = 'flex';
                setTimeout(() => {
                    content.style.transform = 'translateY(0)';
                }, 10);
                console.log('Modal opened');
            } else {
                console.error('Modal elements not found!');
            }
        }

        function closeFilterModal() {
            console.log('Closing modal');
            const modal = document.getElementById('filterModal');
            const content = document.getElementById('modalContent');
            
            if (modal && content) {
                content.style.transform = 'translateY(100%)';
                setTimeout(() => {
                    modal.style.display = 'none';
                }, 300);
                console.log('Modal closed');
            }
        }

        function closeOnBackdrop(event) {
            if (event.target.id === 'filterModal') {
                closeFilterModal();
            }
        }

        function applyFilter() {
            console.log('Applying filter');
            const selected = document.querySelector('input[name="lahan"]:checked');
            const label = document.getElementById('filterLabel');
            
            if (selected && label) {
                const text = selected.parentElement.querySelector('span').textContent;
                label.textContent = text;
                console.log('Filter applied:', text);
            }
            
            closeFilterModal();
        }

        // Test on page load
        window.addEventListener('load', function() {
            console.log('Page fully loaded');
            console.log('Modal element:', document.getElementById('filterModal'));
            console.log('Filter button exists:', true);
        });
</script>

