            <!-- Text -->
            <p class="text-center text-black text-[14px] font-medium mb-6">
                Apakah Anda yakin ingin keluar?
            </p>

            <!-- Buttons -->
            <div class="flex flex-col gap-3">
                <!-- Button Keluar -->
                <button onclick="confirmLogout()" class="w-full bg-hijau-tua text-white py-3 rounded-full text-[14px] font-medium hover:bg-[#2d6b4f] transition-colors">
                    Keluar
                </button>

                <!-- Button Batal -->
                <button onclick="closeLogoutModal()" class="w-full bg-transparent text-hijau-tua py-3 rounded-full text-[14px] font-medium border-2 border-[#1B4332] hover:bg-[#F5FFF5] transition-colors">
                    Batal
                </button>
            </div>
        </div>
    </div>


    <script>
        function openLogoutModal() {
            document.getElementById('logoutModal').style.display = 'flex';
        }

        function closeLogoutModal() {
            document.getElementById('logoutModal').style.display = 'none';
        }

        function confirmLogout() {
            console.log('Logout confirmed');
            closeLogoutModal();
            // Tambahkan logika logout di sini jika diperlukan
        }

        // Tutup modal jika klik di luar
        document.getElementById('logoutModal').addEventListener('click', function(e) {
            if (e.target === this) {
                closeLogoutModal();
            }
        });
    </script>

@endsection
           
